import React, { useState } from "react";
import WeatherIcon from "./WeatherIcon";
import "./WeekForecast.css";
import axios from "axios";
import FormattedDate from "./FormattedDate";

export default function WeekForecast(props) {
let [loaded, setLoaded] = useState(false);
let [forecast, setForecast] = useState(null);

    function handleResponse(response) {
          console.log(response.data);
          setForecast(response.data.daily);
          setLoaded(true);
    }

    if (loaded) { return (
        <div className="WeekForecast">
            <div className="row">
                <div className="col">
                    <div className="WeekForecast-day"><FormattedDate date={forecast[0].time} /></div>
                    <WeatherIcon code={forecast[0].condition.icon} className="small-icon"/>
                    <div className="WeekForecast-temperatures">
                        <span className="WeekForecast-temp-max">{Math.round(forecast[0].temperature.maximum)}°</span>
                        <span className="WeekForecast-temp-min">{Math.round(forecast[0].temperature.minimum)}°</span>
                    </div>
                </div>
            </div>
        </div>)

} else {
    let city = "Elounda";
    const apiKey = "8o2ff7498032b5ae4f9bt64daad694e7";
    let apiUrl = `https://api.shecodes.io/weather/v1/forecast?query=${city}&key=${apiKey}`;

    axios.get(apiUrl).then(handleResponse);
    return null;
}}
