import React, { useState } from "react";
import WeatherIcon from "./WeatherIcon";
import "./WeekForecast.css";
import axios from "axios";

export default function WeekForecast(props) {
const [city, setCity] = useState(props.defaultCity);
  const [weatherData, setWeatherData] = useState({ ready: false });
    function handleResponse(response) {
        console.log(response.data);
        setWeatherData({
            ready: true,
            icon: response.data.condition.icon,
            wind: response.data.wind.speed,
            date: new Date(response.data.time * 1000),
            humidity: response.data.temperature.humidity,
            feelsLike: response.data.temperature.feels_like,
            city: response.data.city,
          });
    }
    const apiKey = "8o2ff7498032b5ae4f9bt64daad694e7";
    let apiUrl = `https://api.shecodes.io/weather/v1/current?query=${city}&key=${apiKey}`;

    axios.get(apiUrl).then(handleResponse);

    return (
        <div className="WeekForecast">
            <div className="row">
                <div className="col">
                    <div className="WeekForecast-day">Thu</div>
                    <WeatherIcon code="clear-sky-day" className="small-icon"/>
                    <div className="WeekForecast-temperatures">
                        <span className="WeekForecast-temp-max">15°</span>
                        <span className="WeekForecast-temp-min">11°</span>
                    </div>
                </div>
            </div>
        </div>
        )
}
