import { keyframes, css } from "@emotion/react";

export const flashKeyframes = keyframes`
  0%, 50%, 100% {
    opacity: 1;
  }
  25%, 75% {
    opacity: 0;
  }
`;

export const flash = css`
  &.flash {
    animation-name: ${flashKeyframes};
  }
`;

import { keyframes, css } from "@emotion/react";

const floatKeyframes = keyframes`
  0% {
    transform: translate(0px, calc(var(--cloud-push) + 15px));
    opacity: 0.75;
  }
  25% {
    transform: translate(15px, calc(var(--cloud-push) + 0px));
    opacity: 1;
  }
  50% {
    transform: translate(30px, calc(var(--cloud-push) + 0px));
    opacity: 1;
  }
  75% {
    transform: translate(25px, calc(var(--cloud-push) + 0px));
    opacity: 1;
  }
  100% {
    transform: translate(0px, calc(var(--cloud-push) + 15px));
    opacity: 0.75;
  }
`;

const floatReverseKeyframes = keyframes`
  0% {
    transform: translate(10px, 10px) scale(0.5);
    opacity: 0.75;
  }
  25% {
    transform: translate(15px, 15px) scale(0.5);
    opacity: 1;
  }
  50% {
    transform: translate(10px, 15px) scale(0.5);
    opacity: 1;
  }
  75% {
    transform: translate(0px, 15px) scale(0.5);
    opacity: 1;
  }
  100% {
    transform: translate(10px, 10px) scale(0.5);
    opacity: 0.75;
  }
`

export const float = css`
  &.float {
    animation-name: ${floatKeyframes};
    &.reverse {
      animation-name: ${floatReverseKeyframes};
    }
  }
`;

import { keyframes, css } from '@emotion/react';

export const rotateKeyframes = keyframes`
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
`;

export const rotate = css`
  &.rotate {
    animation-name: ${rotateKeyframes};
  }
`;

import { keyframes, css } from "@emotion/react";

export const fadeInOutKeyframes = keyframes`
  0%, 100% {
    stroke-opacity: 0;
    fill-opacity: 0;
  }
  50% {
    stroke-opacity: 1;
    fill-opacity: 1;
  }
`;

export const fadeInOut = css`
  &.fade-in-out {
    animation-name: ${fadeInOutKeyframes};
  }
`;

import { keyframes, css } from "@emotion/react";

export const bobKeyframes = keyframes`
  0%,
  100% {
    transform: translateY(0) scale(1);
  }
  50% {
    transform: translateY(3%) scale(0.95);
  }
`;


export const bob = css`
  &.bob {
    animation: ${bobKeyframes} 3s ease-in-out infinite;
  }
`;

import { css } from '@emotion/react';
import { flash } from './flash';
import { float } from './float';
import { rotate } from './rotate';
import { fadeInOut } from './fade-in-out';
import { bob } from './bob';

export const globalStyles = css`
  .animated {
    animation-duration: 1s;
    animation-fill-mode: both;
    &.infinite {
      animation-iteration-count: infinite;
    }
    ${Array.from({ length: 6 }, (_, i) => i + 1).map(
      (i) => `
        &.delay-${i}s {
          animation-delay: ${i}s;
        }
      `
    ).join('')}
    ${float}
    ${flash}
    ${rotate}
    ${fadeInOut}
    ${bob}
  }
`;

import { css } from "@emotion/react";
import type { ShapeType } from "../type";

export const lighteningBolt: ShapeType = {
  styles: css`
    .lightening {
      animation-duration: 2.5s;
    }
  `,
  defs: <></>,
  symbol: <symbol id="lighteningBolt">
    <path fill="#b8f1f8" d="M15,0 L1,23 L8,23 L0,40 L15,19 L8,19Z" />
  </symbol>
};

import { css, keyframes } from "@emotion/react";
import type { ShapeType } from "../type";
const rayMoveAmount = 2.5;

const weatherRay1 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translateY(-${rayMoveAmount}%);
  }
  `;

const weatherRay2 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translate(-${rayMoveAmount}% * 0.5, ${rayMoveAmount}% * 0.5);
  }
`;

const weatherRay3 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translateX(${rayMoveAmount}%);
  }
`;

const weatherRay4 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translate(-${rayMoveAmount}% * 0.5, -${rayMoveAmount}% * 0.5);
  }
`;

const weatherRay5 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translateY(${rayMoveAmount}%);
  }
`;

const weatherRay6 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translate(${rayMoveAmount}% * 0.5, -${rayMoveAmount}% * 0.5);
  }
`;

const weatherRay7 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translateX(-${rayMoveAmount}%);
  }
`;

const weatherRay8 = keyframes`
  0%,
  100% {
    transform: translate(0%);
  }
  50% {
    transform: translate(${rayMoveAmount}% * 0.5, ${rayMoveAmount}% * 0.5);
  }
`;


export const sun: ShapeType = {
  styles: css`
    .sun-mask-circle {
      fill: black;
      transition: transform 0.5s ease-in-out;
      transform: scale(1);
      transform-origin: 50% 50%;
      stroke: black;
      stroke-width: 6;
      stroke-linecap: round;
      stroke-linejoin: round;
    }
    .sun {
      .sun-circle {
        fill: transparent;
        transition: transform 0.5s ease-in-out;
        transform: scale(1);
        transform-origin: 50% 50%;
        stroke: url(#gradYellow);
      }
      .path {
        stroke-width: 6;
        stroke-linecap: round;
        stroke-linejoin: round;
      }
      .rays {
        transform-origin: 50%;
        animation-duration: 120s;
        animation-timing-function: linear;
      }

      .ray {
        transition: transform 0.5s ease-in-out;
        transform: scale(1);
        transform-origin: 50% 50%;
        stroke: yellow;
        &:nth-of-type(1) {
          animation: ${weatherRay1} 5s infinite 0s;
        }
        &:nth-of-type(2) {
          animation: ${weatherRay2} 5s infinite 0.1s;
        }
        &:nth-of-type(3) {
          animation: ${weatherRay3} 5s infinite 0.2s;
        }
        &:nth-of-type(4) {
          animation: ${weatherRay4} 5s infinite 0.3s;
        }
        &:nth-of-type(5) {
          animation: ${weatherRay5} 5s infinite 0.4s;
        }
        &:nth-of-type(6) {
          animation: ${weatherRay6} 5s infinite 0.5s;
        }
        &:nth-of-type(7) {
          animation: ${weatherRay7} 5s infinite 0.6s;
        }
        &:nth-of-type(8) {
          animation: ${weatherRay8} 5s infinite 0.7s;
        }
      }
    }
  `,
  defs: <mask className="sun-mask" id="sun-mask">
    <rect x="0" y="0" width="100" height="100" fill="white"/>
    <circle className="sun-mask-circle" cx="50" cy="48.5" r="16.5"/>
  </mask>,
  symbol: <symbol id="sun">
    <g className="sun-mask">
        <g className="sun">
            <g className="rays-mask" mask="url(#sun-mask)">
                <g className="rays animated infinite rotate">
                    <line className="path ray" x1="50" y1="12.3" x2="50" y2="18.9"/>
                    <line className="path ray" x1="75.7" y1="22.8" x2="70.9" y2="27.6"/>
                    <line className="path ray" x1="86.2" y1="48.5" x2="79.6" y2="48.5"/>
                    <line className="path ray" x1="75.7" y1="74.2" x2="70.9" y2="69.4"/>
                    <line className="path ray" x1="50" y1="78.1" x2="50" y2="84.7"/>
                    <line className="path ray" x1="29.1" y1="69.4" x2="24.3" y2="74.2"/>
                    <line className="path ray" x1="20.4" y1="48.5" x2="13.8" y2="48.5"/>
                    <line className="path ray" x1="29.1" y1="27.6" x2="24.3" y2="22.8"/>
                </g>
            </g>
            <circle className="path sun-circle" cx="50" cy="48.5" r="16.5"/>
        </g>
    </g>
  </symbol>
};

import { css, keyframes } from "@emotion/react";
import type { ShapeType } from "../type";
import { fadeInOutKeyframes } from '../../animations/fade-in-out';

const weatherAnimationIncrease = Math.PI * 0.01;
const weatherAnimationStep = 2;
const weatherAnimationSize = 70; // 70% as a decimal for calculations

// Translated hail function
const hailTransform = (xMultiplier: number, weatherAnimationT: number, weatherAnimationI: number) => {
  const eq = Math.sin(weatherAnimationT) * 0.5;
  const x = xMultiplier * (weatherAnimationI - 50); // Assuming the xMultiplier accounts for units
  const y = eq * -(weatherAnimationSize * 0.25) + (weatherAnimationSize * 0.25);
  return `translate(${x}%, ${y}%)`; // Add '%' for pixel units
};

// Function to generate keyframes
const generateHailKeyframes = (xMultiplier: number) => {
  let weatherAnimationI = 51;
  let weatherAnimationT = 0;
  let keyframesString = `0% { transform: translate(0, 0); }\n`;

  while (weatherAnimationI <= 100) {
    const transform = hailTransform(xMultiplier, weatherAnimationT, weatherAnimationI);
    keyframesString += `${weatherAnimationI}% { transform: ${transform} }\n`;
    weatherAnimationT += weatherAnimationIncrease * weatherAnimationStep * 2;
    weatherAnimationI += weatherAnimationStep;
  }
  // Emotion's keyframes template literal should not include the keyframes prefix
  return keyframes`${keyframesString}`;
};

const weatherHailLeft = generateHailKeyframes(-0.3);
const weatherHailRight = generateHailKeyframes(0.3);
const weatherHailMiddle = generateHailKeyframes(-0.15);
const weatherHailMiddle2 = generateHailKeyframes(0.15);


export const hail: ShapeType = {
  styles: css`
    .hail {
      transform: translateY(5%);
      fill: #eee;
      fill-opacity: 0;
      animation: 1s ease-in infinite;

      &:nth-of-type(1),
      &:nth-of-type(4) {
        animation-name: ${weatherHailLeft}, ${fadeInOutKeyframes};
      }

      &:nth-of-type(2) {
        animation-name: ${weatherHailMiddle}, ${fadeInOutKeyframes};
      }

      &:nth-of-type(5) {
        animation-name: ${weatherHailMiddle2}, ${fadeInOutKeyframes};
      }

      &:nth-of-type(3),
      &:nth-of-type(6) {
        animation-name: ${weatherHailRight}, ${fadeInOutKeyframes};
      }

      ${Array.from({ length: 6 }, (_, i) => i + 1).map(
        (i) => `
          &:nth-of-type(${i}) {
            animation-delay: ${1 * (1 / 6) * i - 1 / 6}s;
          }
        `
      ).join('')}
    }
  `,
  symbol: <symbol id="hail">
    <circle className="hail" cx="43.4" cy="69.9" r="1.6"/>
    <circle className="hail" cx="51.6" cy="69.9" r="1.6"/>
    <circle className="hail" cx="59.9" cy="69.9" r="1.6"/>
    <circle className="hail" cx="43.4" cy="69.9" r="1.6"/>
    <circle className="hail" cx="51.6" cy="69.9" r="1.6"/>
    <circle className="hail" cx="59.9" cy="69.9" r="1.6"/>
  </symbol>
};

import { css } from "@emotion/react";
import type { ShapeType } from "../type";

export const star: ShapeType = {
  styles: css`
    .stars {
      animation-duration: 3s;
    }
  `,
  defs: <>
  </>,
  symbol: <symbol id="star">
    <polygon points="5,0 2,10 10,4 0,4 8,10" style={{
      fill: 'url(#gradYellow)',
      fillRule: 'nonzero'
    }}/>
  </symbol>
};

import { css } from "@emotion/react";
import type { ShapeType } from "../type";


export const clouds: ShapeType = {
  styles: css`
    .small-cloud {
      animation-duration: 15s;
      animation-timing-function: linear;
      --cloud-push: 0px;
      &.push {
        --cloud-push: 7px;
      }
      &.reverse {
        animation-duration: 10s;
        animation-timing-function: linear;
      }
    }
  `,
  defs: <>
  </>,
  symbol: <>
    <symbol id="grayCloud">
      <path d="M20,15 Q25,0 45,11 Q60,5 60,20 A30,15 5 1,1 20,15 Z" />
